import os

def delete_files_permanently(directory, dry_run=False):
  """
  Permanently deletes files and directories within a specified directory.

  Args:
    directory (str): The path to the directory to operate on.
    dry_run (bool): If True, prints details of proposed deletions instead 
                    of actually deleting them. Defaults to False (non-interactive).


  Returns: None. Prints messages to standard output.

  Raises:
    TypeError: if input is not a string or iterable for directory
    ValueError: if directory does not exist or is not a file/dir
  """

  if not isinstance(directory, str):
      raise TypeError("Directory must be a string.")
  if not os.path.exists(directory):
      raise ValueError(f"Directory '{directory}' does not exist.")
  if not os.path.isfile(directory) or not os.path.isdir(directory):
    raise ValueError(f"'{directory}' is not a valid file or directory.")



  try:
    for item in os.listdir(directory):
      full_path = os.path.join(directory, item)

      if os.path.isfile(full_path):
        try:
          os.remove(full_path) # Attempt deletion - handles potential permissions issues gracefully
          print(f"Deleted: {full_path}")
        except Exception as e:
            if dry_run:
                print(f"Dry Run: Would have deleted {full_path} but it couldn't.")
            else:
              print(f"Error deleting {full_path}: {e}")


      elif os.path.isdir(full_path):
        # Recursively call the function for subdirectories
        delete_files_permanently(full_path, dry_run=True)  # Default to no true recursive delete
  finally:
    pass # No cleanup needed after iteration completes


if __name__ == "__main__":
  # Example Usage (Non-Interactive Mode)
  my_directory = "C:\\MyFolder"   # Replace with your target directory
  delete_files_permanently(my_directory)
  print("\nFinished processing.\n")


